import React, {useEffect,useState} from "react";
// import Button from 'react-bootstrap/Button'

// components
// import Coursepic from "components/Course/Coursepic";

export default function Addtablecourse() {

  const [addcourse, setAddcourse] = useState(null);
  const [form, setForm] = useState({
    name :"" ,
    // coursetitle : "",
    mentor: "",
    // coursetype : "",
    pricing : "",
    duration : "",
    // topics:"",
    description:"",
    // curriculum:"",
    created_at:"",
    icon_link: ""
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

useEffect(() => {
  fetchCourse();
}, [])

const fetchCourse = async () => {
  setIsSubmitting(true);
  const res = await fetch('https://cds-back.uurl.xyz/api/v1/course');
  // /addcourse');
  const data = await res.json();
  setIsSubmitting(false);
  setAddcourse(data);
}

const handleChange = (e) => {
  setForm({
    ...form,
    [e.target.name]: e.target.value
  })
}

const validate = () => {
  let err = {};


  if(!form.name){
    err.name='course name is required'
  }
  // if(!form.coursetitle){
  //   err.coursetitle='course title is required'
  // }
  if(!form.coursetype){
    err.coursetype='course type is required'
  }
  if(!form.mentor){
    err.mentor='mentor name is required'
  }
  // if(!form.curriculum){
  //   err.curriculum='curriculum is required'
  // }
  if(!form.duration){
    err.duration='course duration is required'
  }
  return err; 
}

const showError = (errorObj) => {
  let errMsg = '';

  for(let err in errorObj){
    errMsg += `${errorObj[err]}. `;
  }

  alert(`Errors ${errMsg}`);

}

const postCourses = async (data) => {
  await fetch('https://cds-back.uurl.xyz/api/v1/course', {
    method: 'POST',
    headers:{
      'Accept' : 'application/json',
      'Content-Type' : 'application/json'
    },
    body : JSON.stringify(data)

  })
}

const handleSubmit = async (e) => {
  e.preventDefault();
  const errors = validate();
  if(Object.keys(errors).length === 0){
    setIsSubmitting(true);
    await postCourses(form);
    setIsSubmitting(false);
    setForm({
      name :"" ,
      // coursetitle : "",
      mentor: "",
      // coursetype : "",
      pricing : "",
      duration : "",
      // topics:"",
      description:"",
      // curriculum:"",
      created_at:"",
    icon_link: ""
    })
    fetchCourse();
  }else{
    showError(errors);
  }
}

  return (
    <>
      <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg bg-gray-200 border-0">
        <div className="rounded-t bg-white mb-0 px-6 py-6">
          <div className="text-center flex justify-between">
            <h6 className="text-gray-800 text-xl font-bold">Add Course</h6>
            <button
              className="bg-blue-500 text-white active:bg-blue-600 font-bold uppercase text-xs px-4 py-2 rounded shadow hover:shadow-md outline-none focus:outline-none mr-1 ease-linear transition-all duration-150"
              type="button" onClick={event =>  window.location.href='/courses'}
            >
              All Courses
            </button>
          </div>
        </div>
        {isSubmitting ? (
          <div><h2>Loading...</h2> </div>
        ) : ('')}
        <div className="flex-auto px-4 lg:px-10 py-10 pt-0">
          <form onSubmit={handleSubmit}>
            <h6 className="text-gray-500 text-sm mt-3 mb-6 font-bold uppercase">
              Course Information
            </h6>
            <div className="flex flex-wrap">
            {/* <div className="w-full lg:w-6/12 px-4">
                <div className="relative w-full mb-3">
                  <label
                    className="block uppercase text-gray-700 text-xs font-bold mb-2"
                    htmlFor="grid-password"
                  >
                    INDEX NUMBER
                  </label>
                  <input
                    type="number"
                    className="px-3 py-3 placeholder-gray-400 text-gray-700 bg-white rounded text-sm shadow focus:outline-none focus:shadow-outline w-full ease-linear transition-all duration-150"
                    name="index"
                    id="index"
                    placeholder="index"
                    onChange={handleChange}
                  />
                </div>
              </div> */}
              <div className="w-full lg:w-6/12 px-4">
                <div className="relative w-full mb-3">
                  <label
                    className="block uppercase text-gray-700 text-xs font-bold mb-2"
                    htmlFor="grid-password"
                  >
                   Course Name
                  </label>
                  <input
                    type="text"
                    className="px-3 py-3 placeholder-gray-400 text-gray-700 bg-white rounded text-sm shadow focus:outline-none focus:shadow-outline w-full ease-linear transition-all duration-150"
                    name="name"
                    id="name"
                    placeholder="Enter Course Name" 
                    onChange={handleChange}
                  />
                </div>
