import React, {useState,useEffect} from "react";
import Axios from 'axios';
//image
import logo from 'static/images/cds logo-02.svg'

// components
import Sidebar from "components/Sidebar/Sidebar.js";

//table
import CardSettings from "components/Mentor/CardSettings.js";

// Susmita __ Taar-kata



export default function Editmentor() {

    const url = 'http://localhost:5000/addmentor'

    const [addmentor, setAddmentor] = useState(null);
    const [data, setData] = useState({
      email : "",
      fullname :"" ,
      mobile : "",
      address: "",
      city : "",
      country : "",
      pincode : "",
      designation:"",
      linkedin:"",
      facebook:"",
      twitter : "",
      mentorpicture:""
    });

    useEffect(() => {
        Axios.get(url)
        .then(res=>{
            setAddmentor(res => {
                setAddmentor(res.data)
            }).catch(err=>(console.log(err)))
        })

    }, []
    )

    function remove(id){
        console.log(id)
        Axios.delete(url+id)
        .then(res =>{
            console.log(res.data)
            const myalldata = MentorList.filter(item => item.id !== id)
            setAddmentor(myalldata)
        }).catch(console.log(err))
    }


    function submit(e){
        e.preventDefault()
        Axios.post(url,data)
        .then(res =>{
            console.log(res.data)
            const mydata = [...MentorList, res.data]
            setAddmentor(mydata)
        }).catch(console.log(err))
    }


    function handle(e){
        const newdata ={...data}
        newdata[e.target.id] = e.target.value
        setData(newdata)
    }

  return (
    <>
      <Sidebar />
        <div className="relative md:ml-64 bg-gray-200">
            <div className="px-4 md:px-10 mx-auto w-full -m-24">
                <br/><br/><br/><br/><br/>
                    {/* logo */}
                    <div className="row">
                        <div className="col-md-12 col-sm-12 col-xs-12">
                            <center>
                                <img src={logo} alt="Logo"  style={{width:'130px', height:'130px'}} /> 
                            </center>
                        </div>
                    </div>
                    {/* heading */}
                        <center><h1><u>EDIT MENTOR</u></h1></center>
                    {/* table */}
                    <div className="flex flex-wrap mt-4">
                        <div className="w-full mb-12 px-4">
                            <CardSettings />
                        </div>
                    </div>
            </div>
        </div>
    </>
  );
}
