import React from "react";
import { DropdownButton, Dropdown} from 'react-bootstrap';
//image
import logo from 'static/images/cds logo-02.svg'

// components
import Sidebar from "components/Sidebar/Sidebar.js";

//table
import Testimonialtable from "components/Testimonial/Testimonialtable.js";


export default function Testimonial() {

    const [apiData, setApiData] = React.useState([]);
  React.useEffect(() => {
    fetch('https://Random_API/Link_From_Dropdown_Goes_Here')
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setApiData(data);
      });
  }, []);
  return (
    <>
      <Sidebar />
        <div className="relative md:ml-64 bg-gray-200">
            <div className="px-4 md:px-10 mx-auto w-full -m-24">
                <br/><br/><br/><br/><br/>
                    {/* logo */}
                    <div className="row">
                        <div className="col-md-12 col-sm-12 col-xs-12">
                            <center>
                                <img src={logo} alt="Logo"  style={{width:'130px', height:'130px'}} /> 
                            </center>
                        </div>
                    </div>
                    {/* heading */}
                        <center><h1><u>ALL TESTIMONIAL</u></h1></center>
                    {/* table */}
                    <div className="flex flex-wrap mt-4">
                        <div className="w-full mb-12 px-4">
                            <Testimonialtable color="dark" />
                        </div>
                    </div>
                    <DropdownButton id="dropdown-item-button" title="API Links" onSelect={function(evt){console.log(evt)}}>
                        <Dropdown.Item as="button" eventKey='API_Link1'>Link 1</Dropdown.Item>
                        <Dropdown.Item as="button" eventKey='API_Link2'>Link 2</Dropdown.Item>
                        <Dropdown.Item as="button" eventKey='API_Link3'>Link 3</Dropdown.Item>
                    </DropdownButton>          
            </div>
        </div>
    </>
  );
}
