import React, {useEffect,useState} from "react";
import { forEachChild } from "typescript";
// import Button from 'react-bootstrap/Button'

// components
// import Coursepic from "components/Course/Coursepic";

export default function Addtablecourse() {

  const [addcourse, setAddcourse] = useState(null);
  const [form, setForm] = useState({
    name :"" ,
    // coursetitle : "",
    mentor: "",
    // coursetype : "",
    pricing : "",
    duration : "",
    // topics:"",
    description:"",
    // curriculum:"",
    created_at:"",
    icon_link: ""
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

useEffect(() => {
  fetchCourse();
}, [])

const fetchCourse = async () => {
  setIsSubmitting(true);
  const res = await fetch('/addcourse');
  // /addcourse');
  const data = await res.json();
  setIsSubmitting(false);
  setAddcourse(data);
}

const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value
    }
    )
}

const uploadImage = async (e) => {
  const file = e.target.files[0]
  const base64 = await convertBase64(file);
  e.target.value = String(base64)
  console.log( e.target.value)
  //  setForm({
  //   ...form,
  //   [e.target.name]: e.target.value
  // }
  // )
  // console.log();
}

const convertBase64 = (file) => {
  return new Promise((resolve,reject) =>{
   const fileReader = new FileReader();
   fileReader.readAsDataURL(file);

   fileReader.onload =() => {
     resolve(fileReader.result);
   };

   fileReader.onerror = (error) => {
     reject(error);
   }
  })
}




const validate = () => {
  let err = {};


  if(!form.name){
    err.name='course name is required'
  }
  // if(!form.coursetitle){
  //   err.coursetitle='course title is required'
  // }
  // if(!form.coursetype){
  //   err.coursetype='course type is required'
  // }
  if(!form.mentor){
    err.mentor='mentor name is required'
  }
  // if(!form.curriculum){
  //   err.curriculum='curriculum is required'
  // }
  if(!form.duration){
    err.duration='course duration is required'
  }
  return err; 
}

const showError = (errorObj) => {
  let errMsg = '';

  for(let err in errorObj){
    errMsg += `${errorObj[err]}. `;
  }

  alert(`Errors ${errMsg}`);

}

const postCourses = async (data) => {
  await fetch('/addcourse', {
    method: 'POST',
    headers:{
      'Accept' : 'application/json',
      'Content-Type' : 'application/json'
    },
    body : JSON.stringify(data)

  })
}

const handleSubmit = async (e) => {
  e.preventDefault();
  const errors = validate();
  if(Object.keys(errors).length === 0){
    setIsSubmitting(true);
    await postCourses(form);
    setIsSubmitting(false);
    setForm({
      name :"" ,
      // coursetitle : "",
      mentor: "",
      // coursetype : "",
      pricing : "",
      duration : "",
      // topics:"",
      description:"",
      // curriculum:"",
      created_at:"",
    icon_link: ""
    })
    fetchCourse();
  }else{
    showError(errors);
  }
}

  return (
    <>
      <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded-lg bg-gray-200 border-0">
        <div className="rounded-t bg-white mb-0 px-6 py-6">
          <div className="text-center flex justify-between">
            <h6 className="text-gray-800 text-xl font-bold">Add Course</h6>
            <button
              className="bg-blue-500 text-white active:bg-blue-600 font-bold uppercase text-xs px-4 py-2 rounded shadow hover:shadow-md outline-none focus:outline-none mr-1 ease-linear transition-all duration-150"
              type="button" onClick={event =>  window.location.href='/courses'}
            >
